
import os
import sys
import math

def arrSplit(arr):
    print(arr.count(0))
    arr.remove(0)
    print(arr)
    return arraySplitting(arr)
def arraySplitting(arr):

    lenArr = len(arr)
    sumArr = sum(arr)
    if lenArr == 1:
        return 0
    if sumArr % 2 != 0:
        return 0
    sumTotal = 0
    count = 0
    for i in range(lenArr):
        sumTotal += arr[i]
        if sumTotal == sumArr//2:
            count += max(arraySplitting(arr[:i+1]),arraySplitting(arr[i+1:]))+1
            break
        elif sumTotal > sumArr//2:
            return 0
    return count

